img_size_db=[]
image_list_temp=[]
total_image_list=[]

def remove_dup(folder_name):
    global image_list_temp
    global total_image_list
    global img_size_db
    image_list_temp=list(set(image_list_temp))
    print(image_list_temp)
    root_path=os.path.join(os.getcwd(),folder_name).replace('\\','/')
    for image_list_temp_ in image_list_temp:
        img_path=os.path.join(root_path,image_list_temp_[0])
        if os.path.exists(img_path):
            file_size=os.path.getsize(img_path)
            #print("TEST : ", img_path,file_size)
            
            is_disallow=False
            for prohibit_site_ in prohibit_site:
                if prohibit_site_ in image_list_temp_[1].lower():
                    is_disallow=True
            
            if (file_size in img_size_db) or (is_disallow==True):
                #print("REMOVE : ",img_path,file_size)
                os.remove(img_path)
            else:
                new_path=os.path.join(os.path.dirname(img_path),str(len(total_image_list)).zfill(6)+os.path.basename(img_path))
                try:
                    os.rename(img_path.replace('\\','/'),new_path.replace('\\','/'))
                except:
                    new_path=img_path
                #print("ADD : ",new_path,file_size)
                img_size_db+=[file_size]
                total_image_list+=[(new_path,image_list_temp_[1])]

    try:os.rmdir(root_path)
    except:pass
                
    image_list_temp=[]


class HanImageDownloader(ImageDownloader):
    def get_filename(self, task, default_ext):
        global image_list_temp
        url_path = urlparse(task['file_url'])[2]
        if '.' in url_path:
            extension = url_path.split('.')[-1]
            if extension.lower() not in [
                    'jpg', 'jpeg', 'png', 'bmp', 'tiff', 'gif', 'ppm', 'pgm'
            ]:
                extension = default_ext
        else:
            extension = default_ext
            
        filename = base64.b64encode(url_path.encode()).decode()
        filename_ext='{}.{}'.format(filename, extension)
        
        image_list_temp+=[(filename_ext,task['file_url'])]
        
        return filename_ext


def crawl_all(subject_keywords_,search_keywords_,lang_):
    google_crawler = GoogleImageCrawler(downloader_cls=HanImageDownloader,downloader_threads=max_thread,storage={'root_dir': 'images/%s%s_google' % (subject_keywords_,lang_)})
    google_crawler.crawl(search_keywords_, max_num=max_crawling_num)
    remove_dup('images/%s%s_google' % (subject_keywords_,lang_))

    bing_crawler = BingImageCrawler(downloader_cls=HanImageDownloader,downloader_threads=max_thread,storage={'root_dir': 'images/%s%s_bing' % (subject_keywords_,lang_)})
    bing_crawler.crawl(search_keywords_, max_num=max_crawling_num)
    remove_dup('images/%s%s_bing' % (subject_keywords_,lang_))

    

for keyword_n_,subject_keywords_ in enumerate(main_keywords):
    img_size_db=[]
    subject_keywords_=subject_keywords_.strip()

    if crawl_melanoma_only==True and (subject_keywords_!='melanoma'):continue

    crawl_all(subject_keywords_,subject_keywords_,'0')
    searched_keyword_list=[subject_keywords_]
    
    for lang_,translate_keyword_ in enumerate(translate_keyword):
        search_keywords_=translate_keyword_[keyword_n_].strip()
        if search_keywords_ in searched_keyword_list:continue
        searched_keyword_list=[search_keywords_]
        crawl_all(subject_keywords_,search_keywords_,str(lang_+1))

        
book_final = Workbook()
sheet_final = book_final.add_sheet('url_filepath', cell_overwrite_ok=True)
for row_,total_image_list_ in enumerate(total_image_list):
    sheet_final.write(row_, 0, total_image_list_[0])
    sheet_final.write(row_, 1, total_image_list_[1])
book_final.save('url_filepath.xls')
